    for (i = 0; i <= max; i++) {
        sum1 += num[i] + 1;
    }
    for (i = 0; i <= min; i++) {
        sum2 += num[i] + 1;
    }
    for (i = sum1 - num[max] - 1; i < sum1 - 1; i++) {
        l[j] = sentense[i];
        j++;
    }
    l[j] = '\0';
    for (i = sum2 - num[min] - 1; i < sum2 - 1; i++) {
        s[k] = sentense[i];
        k++;
