        if (b[i] == max(b, n)) {
            printf("%s\n", s[i]);
            break;
        }
    }
    for (i = 0; i < n; i++) {
        if (b[i] == min(b, n)) {
            printf("%s\n", s[i]);
            break;
        }
    }
    return 0;
}

int max(int a[], int n) {
    int t = a[0], i;
    for (i = 0; i < n; i++) {
        if (t < a[i])
            t = a[i];
    }
    return t;
}

int min(int a[], int n) {
    int t = a[0], i;
    for (i = 0; i < n; i++) {
        if (t > a[i])
            t = a[i];
    }
    return t;
}

