int store(char str[], char sto[], int len) {
    int i, m = 0;
    for (i = 0; i < len; i++)
        if (str[i] >= 'a' && str[i] <= 'z') {
            sto[m] = str[i];
            m++;
        }
    return (m);
}
void sort(char sto[], int m) {
    int i, j, k;
    char t;
    for (i = 0; i < m - 1; i++) {
        k = i;
        for (j = i + 1; j < m; j++)
            if (sto[j] < sto[k])
                k = j;
        if (k != i) {
            t = sto[i];
            sto[i] = sto[k];
            sto[k] = t;
        }
    }
}

