void find(char *str) {
    int i, j, k, len, count = 0;
    int num[26] = {0};

    len = strlen(str);
    for (i = 0; i < len; i++) {
        *(num + count) = (int)*(str + i);
        count++;

        if (count > 1) {
            for (j = 0; j < count - 1; j++) {
                if (*(num + j) == *(num + count - 1)) {
                    for (k = j; k < count - 2; k++) {
                        *(num + k) = *(num + k + 1);
                    }
                    count -= 2;
                }
            }
        }
    }

    if (count == 0) {
        printf("no\n");
    } else {
        printf("%c\n", *num);
    }
}

