struct Top {
    int num;
    float score;
} Top[4] = {{0, 0}};
struct Top *p;

void maopao(int n) {
    int i;
    for (i = 1; i < n; i++) {
        for (p = Top; p < Top + n - i; p++) {
            if ((p->score) < ((p + 1)->score)) {
                Top[3] = *p;
                *p = *(p + 1);
                *(p + 1) = Top[3];
            }
        }
    }
}

void sort(float a, int n) {
    p = Top + 2;
    if (a > (p->score)) {
        p->score = a;
        p->num = n;
    }
    maopao(3);
}

