    scanf("%d", &n);
    head = NULL;
    for (i = 0; i < n; i++) {
        scanf("%d%d%d", &p1->id, &p1->c, &p1->m);
        p1->s = p1->c + p1->m;
        if (i == 0)
            head = p1;
        else
