struct Student *insert(struct Student *head, struct Student *stu) {
    int j = 0;
    struct Student *p0, *p1, *p2;
    p1 = head;
    p0 = stu;
    if (head == NULL) {
        head = p0;
        p0->next = NULL;
    } else {
        while ((p0->total <= p1->total) && (p1->next != NULL) && j < 4) {
            p2 = p1;
            p1 = p1->next;
            j++;
        }
        if (p0->total > p1->total) {
            if (head == p1)
                head = p0;
            else
                p2->next = p0;
            p0->next = p1;
        } else {
            p1->next = p0;
            p0->next = NULL;
        }
    }
    return (head);
}

