}

struct student *compare(struct student *head, struct student *stud) {
    struct student *p0, *p1, *p2;
    int i;
    p1 = head;
    p0 = stud;
    while ((p0->tot <= p1->tot) && (p1->next != NULL)) {
        p2 = p1;
        p1 = p1->next;
    }
    if ((p0->tot > p1->tot) || ((p0->tot = p1->tot) && (p1->next != NULL))) {
        if (head == p1)
            head = p0;
        else
            p2->next = p0;
        p0->next = p1;
        p1 = head;
        for (i = 0; i < 2; i++) {
            p1 = p1->next;
        }
        p1->next = NULL;
    }

    return (head);
}

