        p2->next = p1;
        p1->next = 0;
        num++;
    }
    return (head);
}
struct stu *del(struct stu *head, int max) {
    struct stu *p1, *p2;
    p1 = head;
    while (max != p1->zh && p1->next != 0) {
        p2 = p1;
        p1 = p1->next;
    }
    if (max == p1->zh) {
        if (p1 == head) {
            head = p1->next;
        } else {
            p2->next = p1->next;
        }
    }
    return (head);
}
