struct Student {
    int No;
    int Chinese;
    int Maths;
    int Total;
};

void bubble(struct Student a[], int n) {
    int i, j;
    struct Student temp;
    for (i = 0; i < n - 1; i++) {
        for (j = 0; j < n - 1 - i; j++) {
            if (a[j].Total < a[j + 1].Total) {
                temp = a[j];
                a[j] = a[j + 1];
                a[j + 1] = temp;
            }
        }
    }
}

