        scanf("%ld %d %d", &p0->num, &p0->scorec, &p0->scorem);
        while ((p0->scorec + p0->scorem) <= (p1->scorec + p1->scorem) &&
               (p1->next != NULL)) {
            p2 = p1;
            p1 = p1->next;
        }
        if ((p0->scorec + p0->scorem) > (p1->scorec + p1->scorem)) {
            if (head == p1)
                head = p0;
            else
                p2->next = p0;
            p0->next = p1;
        } else {
            p1->next = p0;
            p0->next = NULL;
        }
        i++;
    }
