}
struct student *sort(struct student *head, int n) {
    struct student *p0, *p1, *p2;
    int i = 3;
    while (i < n) {
        p1 = head;
        p0 = (struct student *)malloc(len);
        scanf("%ld %d %d", &p0->num, &p0->scorec, &p0->scorem);
        while ((p0->scorec + p0->scorem) <= (p1->scorec + p1->scorem) &&
               (p1->next != NULL)) {
            p1 = p1->next;
        }
        if ((p0->scorec + p0->scorem) > (p1->scorec + p1->scorem)) {

            if (head == p1) {
                p2 = p1->next;
                (*p2->next).num = p2->num;
                (*p2->next).scorec = p2->scorec;
                (*p2->next).scorem = p2->scorem;
                p2->num = p1->num;
                p2->scorec = p1->scorec;
                p2->scorem = p1->scorem;
                p1->num = p0->num;
                p1->scorec = p0->scorec;
                p1->scorem = p0->scorem;
            } else if (p1 == head->next) {
                (*p1->next).num = p1->num;
                (*p1->next).scorec = p1->scorec;
                (*p1->next).scorem = p1->scorem;
                p1->num = p0->num;
                p1->scorec = p0->scorec;
                p1->scorem = p0->scorem;
            } else {
                p1->num = p0->num;
                p1->scorec = p0->scorec;
                p1->scorem = p0->scorem;
            }
        }
        free(p0);
        i++;
    }
    return (head);
}

