struct student *del(struct student *head, int num) {
    struct student *p1, *p2;
    p1 = head;
    while (num != p1->num && p1->next != NULL) {
        p2 = p1;
        p1 = p1->next;
    }
    if (num == p1->num) {
        if (p1 == head)
            head = p1->next;
        else
            p2->next = p1->next;
        m = m - 1;
    }
    return (head);
}
