char lower(char x) {
    if (x >= 'A' && x <= 'Z')
        return x + 'a' - 'A';
    return x;
}

char tochar(int m) {
    if (m > 0)
        return '>';
    if (m < 0)
        return '<';
    return '=';
}

void toLowerArray(char x[], int len) {
    for (int j = 0; j < len; j++)
        x[j] = lower(x[j]);
}

