    for (t1 = t; *t1 != '\0'; t1++)
        if (*t1 >= 'A' && *t1 <= 'Z')
            *t1 = *t1 + 32;
    for (; *s == *t && *s != '\0'; s++, t++)
        ;
    return (*s - *t);
}
