int compare(char s1[], char s2[]) {
    int k = 0;
    for (int i = 0; s1[i] != '\0'; ++i) {
        if (s1[i] >= 65 && s1[i] <= 90)
            s1[i] += 32;
    }
    for (int i = 0; s2[i] != '\0'; ++i) {
        if (s2[i] >= 65 && s2[i] <= 90)
            s2[i] += 32;
    }
    k = strcmp(s1, s2);
    return k;
}

