        scanf("%d", &xx[i]);
    for (i = 0; i < y; i++)
        scanf("%d", &yy[i]);
}

void arr(int ddd[], int x) {
    int i, j, min, k;
    for (i = 0; i < x - 1; i++) {
        min = ddd[i];
        for (j = i + 1, k = i; j < x; j++) {
            if (ddd[j] < min) {
                min = ddd[j];
                k = j;
            }
        }
        ddd[k] = ddd[i];
        ddd[i] = min;
    }
}

void con(int aa[], int x, int bb[], int y) {
    int i;
    for (i = x; i < x + y; i++)
        aa[i] = bb[i - x];
    x = x + y;
}

