void scan_(int m, int *a, int n, int *b) {
    int i, j, *p; //????????????

    p = a;
    for (i = 0; i < m; i++) //???????????????????? ??
        scanf("%d", p++);
    p = b;
    for (j = 0; j < n; j++)
        scanf("%d", p++);
}

void sort_(int m, int *a, int n, int *b) {
    int i, j, t, *q;
    for (i = 0; i < m - 1; i++) {
        q = a;
        for (j = 0; j < m - 1 - i; j++) {
            if (*q > *(q + 1)) {
                t = *q;
                *q = *(q + 1);
                *(q + 1) = t;
            }
            q++;
        }
    }

    for (i = 0; i < n - 1; i++) {
        q = b;
        for (j = 0; j < n - 1 - i; j++) {
            if (*q > *(q + 1)) {
                t = *q;
                *q = *(q + 1);
                *(q + 1) = t;
            }
            q++;
        }
    }
}

void print_(int m, int *a, int n, int *b) {
    int i, *p;
    printf("%d", *a);
    p = a + 1;
    for (i = 1; i < m; i++, p++)
        printf(" %d", *p);
    p = b;
    for (i = 0; i < n; i++, p++)
        printf(" %d", *p);
    printf("\n");
}

