void place(void) {
    int s;
    for (i = 1; i < m; i++) {
        for (j = 0; j < m - i; j++) {
            if (a[j] > a[j + 1]) {
                s = a[j];
                a[j] = a[j + 1];
                a[j + 1] = s;
            }
        }
