    for (i = 1; i < n; i++) {
        for (j = 0; j < n - i; j++) {
            if (b[j] > b[j + 1]) {
                s = b[j];
                b[j] = b[j + 1];
                b[j + 1] = s;
            }
        }
    }
    return;
}
void together(void) {
    for (i = 0; i < m; i++) {
        c[i] = a[i];
    }
    for (i = m; i < m + n; i++) {
        c[i] = b[i - m];
    }
    return;
}
