void Qsort(int start, int length, int a[]) {
    int x = a[start];
    int i, j;
    i = start;
    j = length - 1;
    while (i < j) {
        if (x < a[j])
            j--;
        else if (x > a[j]) {
            a[i] = a[j];
            a[j] = x;
            i++;
        } else if (x < a[i]) {
            a[j] = a[i];
            a[i] = x;
            j--;
        } else
            i++;
    }
    if (start < length - 1) {
        Qsort(start, i, a);
        Qsort(i + 1, length, a);
    }
}

