    int i, j, k, t;
    for (i = 0; i < (*x - 1); i++) {
        k = i;
        for (j = i + 1; j < (*x); j++)
            if (*(p + j) < *(p + k))
                k = j;
        if (k != i) {
            t = *(p + i);
            *(p + i) = *(p + k);
            *(p + k) = t;
        }
    }
    for (i = 0; i < *(x + 1) - 1; i++) {
        k = i;
        for (j = i + 1; j < *(x + 1); j++)
            if (*(q + j) < *(q + k))
                k = j;
        if (k != i) {
            t = *(q + i);
            *(q + i) = *(q + k);
            *(q + k) = t;
        }
    }
}
void fold(int *p, int *x, int *q, int *w) {
    int i;
    for (i = 0; i < *x; i++, w++, p++)
        *w = *p;
    for (; i < (*x) + (*(x + 1)); i++, w++, q++)
        *w = *q;
}
