void xu(int x[], int y) {
    int *p, *q, *temp, t;
    p = x;
    for (i = 0; i < y - 1; i++) {
        p = &x[i];
        for (j = i + 1; j < y; j++) {
            q = &x[j];
            if (*p > *q) {
                temp = p;
                p = q;
                q = temp;
            }
        }
        if (p != &x[i]) {
            t = *p;
            *p = x[i];
            x[i] = t;
        }
    }
}

