int main() {
    int s1[100], s2[100];
    int n1, n2;
    scanf("%d%d", &n1, &n2);
    read(s1, n1);
    read(s2, n2);
    arrange(s1, n1);
    arrange(s2, n2);
    connect(s1, s2, n1, n2);
    print(s1, n1 + n2);
