void paixu(int a[], int m) {
    int *p = a, b, c, d;
    for (b = 0; b < m - 1; b++) {
        for (c = 0; c < m - 1 - b; c++) {
            if (*(p + c) > *(p + c + 1)) {
                d = *(p + c);
                *(p + c) = *(p + c + 1);
                *(p + c + 1) = d;
            }
        }
    }
}
void hebing(int a[], int b[], int c[], int m, int n) {
    int *r = a, *s = b, *t = c, p, q;
    for (p = 0; p < m; p++) {
        *(t + p) = *(r + p);
    }
    for (q = 0; q < n; q++) {
        *(t + m + q) = *(s + q);
    }
}
