void sort(int *p, int n) {
    int i, j, temp;
    for (i = n - 1; i > 0; i--)
        for (j = 0; j < i; j++) {
            if (*(p + j) > *(p + j + 1)) {
                temp = *(p + j);
                *(p + j) = *(p + j + 1);
                *(p + j + 1) = temp;
            }
        }
}

