                c[i + 1] = r;
            }
            i++;
        }
        t++;
    } while (t < a - 1);
    t = 0;
    do {
        i = 0;
        while (i < b - 1 - t) {
            if (d[i] > d[i + 1]) {
                r = d[i];
                d[i] = d[i + 1];
                d[i + 1] = r;
            }
            i++;
        }
        t++;
    } while (t < b - 1);
    printf("%d", c[0]);
    i = 1;
    do {
        printf(" %d", c[i]);
        i++;
    } while (i < a);
    i = 0;
    do {
        printf(" %d", d[i]);
        i++;
    } while (i < b);
    return 0;
}

