    for (i = 0; i < y; i++) {
        scanf("%d", &b[i]);
    }
}
void px(int a[100], int x) {
    int i, j, temp;
    for (i = x - 1; i > 0; i--) {
        for (j = 0; j < i; j++) {
            if (a[j] > a[j + 1]) {
                temp = a[j];
                a[j] = a[j + 1];
                a[j + 1] = temp;
            }
        }
    }
}
void hb(int a[100], int b[100], int c[200], int x, int y) {
    int i;
    for (i = 0; i < x + y; i++) {
        if (i < x)
            c[i] = a[i];
        else
            c[i] = b[i - x];
    }
}
void sc(int c[200], int x) {
    int i;
    for (i = 0; i < x; i++) {
        if (i == 0)
            printf("%d", c[i]);
        else
            printf(" %d", c[i]);
    }
}

