void scan(int N1[], int N2[], int *p1, int *p2) {
    int i;
    scanf("%d%d", p1, p2);
    for (i = 0; i < *p1; i++)
        scanf("%d", &N1[i]);
    for (i = 0; i < *p2; i++)
        scanf("%d", &N2[i]);
}
void order(int N1[], int n1, int N2[], int n2) {
    int i, j, imin, Nmin, Nm;
    for (i = 0; i < n1; i++) {
        imin = i;
        Nmin = N1[i];
        for (j = i; j < n1; j++) {
            if (N1[j] < Nmin) {
                imin = j;
                Nmin = N1[j];
            }
        }
        Nm = N1[i];
        N1[i] = N1[imin];
        N1[imin] = Nm;
    }
    for (i = 0; i < n2; i++) {
        imin = i;
        Nmin = N2[i];
        for (j = i; j < n2; j++) {
            if (N2[j] < Nmin) {
                imin = j;
                Nmin = N2[j];
            }
        }
        Nm = N2[i];
        N2[i] = N2[imin];
        N2[imin] = Nm;
    }
}
