    int n, m, p;
    for (n = 2; n <= (*a); n++) {
        for (m = n + 1; m <= (*a) + 1; m++) {
            if ((*(a + n)) > (*(a + m))) {
                p = *(a + n);
                *(a + n) = *(a + m);
                *(a + m) = p;
            }
        }
    }
    for (n = (*a) + 2; n <= (*a) + (*(a + 1)); n++) {
        for (m = n + 1; m <= (*a) + (*(a + 1)) + 1; m++) {
            if ((*(a + n)) > (*(a + m))) {
                p = *(a + n);
                *(a + n) = *(a + m);
                *(a + m) = p;
            }
        }
    }
    return a;
}
