    for (i = 0; i < *pm; i++)
        scanf("%d", p1 + i);
    for (j = 0; j < *pn; j++)
        scanf("%d", p2 + j);
}

void sort(int *pm, int *pn, int *p1, int *p2) {
    int i, j, k, tmp;
    for (i = 0; i < *pm - 1; i++) {
        k = i;
        for (j = i + 1; j < *pm; j++)
            if (*(p1 + j) < *(p1 + k))
                k = j;
        if (k != i) {
            tmp = *(p1 + k);
            *(p1 + k) = *(p1 + i);
            *(p1 + i) = tmp;
        }
    }
    for (i = 0; i < *pn - 1; i++) {
        k = i;
        for (j = i + 1; j < *pn; j++)
            if (*(p2 + j) < *(p2 + k))
                k = j;
        if (k != i) {
            tmp = *(p2 + k);
            *(p2 + k) = *(p2 + i);
            *(p2 + i) = tmp;
        }
    }
}

int combine(int *pm, int *pn, int *p1, int *p2, int *pnew) {
    int i, j;
    for (i = 0; i < *pm; i++)
        *(pnew + i) = *(p1 + i);
    for (j = 0; j < *pn; j++)
        *(pnew + i + j) = *(p2 + j);
    return i + j;
}

