int f(int x[], int X, int y[], int Y) {
    int j;
    arr(x, X);
    arr(y, Y);

    for (j = X; j < X + Y; j++)
        x[j] = y[j - X];
}
void arr(int k[], int K) {
    int i, j, t, d;
    for (i = 0; i < K - 1; i++) {
        d = i;
        for (j = i + 1; j < K; j++)
            if (k[j] < k[d])
                d = j;
        t = k[d];
        k[d] = k[i];
        k[i] = t;
    }
}

