int exchange(int m[], int n) {
    int i, j, l;
    for (i = 0; i < n - 1; i++) {
        for (j = i + 1; j < n; j++) {
            if (m[i] > m[j]) {
                l = m[i];
                m[i] = m[j];
                m[j] = l;
            }
        }
    }
    return m;
}

int add(int x[], int y[], int e, int f) {
    int g;
    for (g = 0; g < f; g++) {
        x[e + g] = y[g];
    }
    return x;
}

