void xu(int a[], int l) {
    int j = 0, k, t;
    while (j < l) {
        k = j + 1;
        while (k < l) {
            if (*(a + j) > *(a + k)) {
                t = *(a + j);
                *(a + j) = *(a + k);
                *(a + k) = t;
            }
            k++;
        }
        j++;
    }
}

