    if (n == 1) {
        cout << "End" << endl;
        return;
    }
    if (n % 2 == 0) {
        m = n / 2;
        cout << n << '/' << 2 << '=' << m << endl;
    } else {
        m = n * 3 + 1;
        cout << n << "*3+1=" << m << endl;
    }
    process(m);
}
int main() {
    int n;
    cin >> n;
    process(n);
    return 0;
}

