        counter = counter + 1;
        trail[counter] = n;
        return functio(n / 2);
    }
    if (n % 2 == 1 && n > 2) {
        counter = counter + 1;
        trail[counter] = n;
        return functio(3 * n + 1);
    }
    if (n == 1) {
        counter = counter + 1;
        trail[counter] = 1;
        return 0;
    }
}
